import React, { useContext } from 'react'
import { AppContext } from '../context/AppContext'
import Spinner from './Spinner';
import { NavLink } from 'react-router-dom'
import "./Blogs.css"

function Blogs(){


  const {posts,loading} = useContext(AppContext);


  return (
    <div className='w-11/12 max-w-[670px] h-screen py-8 flex flex-col gap-y-7 mt-[66px] mb-[70px]  justify-center items-center'>

       { loading ?  (<Spinner />) : ( 

            posts.length === 0 ? (
                                  <div className="min-h-[80vh] w-full flex justify-center items-center">
                                  <p className="text-center font-bold text-3xl">No Blogs Found !</p>
                                  </div> ) : (

                                    posts.map( (post) => (
                                          <div key={post.id}>
                                            <NavLink to={`/blog/${post.id}`} className="font-bold text-lg " > <span>{post.title}</span>  </NavLink>



                                            <p className='text-sm mt-[4px]'>  By <span className='italic'>{post.author}</span> on  <NavLink to={`/categories/${post.category.replaceAll(" ","-")}`}> <span className='underline font-bold'>{post.category} </span> </NavLink> </p>
                                            <p className='text-sm mt-[4px]'>Posted on {post.date}</p>        
                                            <p className='text-md mt-[14px]'>{post.content}</p>          

                                            <div className='flex gap-x-3'>
                                                {post.tags.map( (tag) => (
                                                     <NavLink key={index} to={`/tags/${tag.replaceAll(" ","-")}`}> <span className="text-blue-700 underline font-bold text-xs mt-[5px]">{`#${tag}`}</span> </NavLink>           // its like #dev , #cp
                                                        ))}
                                            </div>

                                          </div>
                                        ))
                            )
                    )}
    </div>

  )}

export default Blogs
